		},
		{
			"id": "1c109904-d12f-482d-9dcd-ea63ab7cd78d",
			"name": "1c109904-d12f-482d-9dcd-ea63ab7cd78d",
			"type": "facts",
			"content": {
				"number": "1",
				"title": "<a class=\"crossref\" id=\"62372\"></a>Vad styr halveringstiden?",
				"text": "<table><tbody><tr><td><p>t<sub>1/2</sub> = 0,7 x Distributionsvolym / Clearance</p></td></tr></tbody></table>",
				"id": "x2_15"
			},
			"settings": {
				"preprocessors": {
					"idinjection.js": "false"
				},
				"postprocessors": {
