function covfunc = gp_cov_delta(N)

covfunc = @get_covariance;

  function varargout = get_covariance(theta)

  varargout = cell(nargout,1);

  if nargin == 0
    % Return the number of parameters and the size of the covariance matrix
    if nargout >= 1
      varargout{1} = 0; % number of parameters
      if nargout >= 2
        varargout{2} = N; % number of rows
        if nargout == 3
          varargout{3} = N; % number of columns
        else
          error('Too many outputs');
        end
      end
    end
    return
  end

  K = speye(N);
  varargout{1} = K;
  if nargout >= 2
    dK = cell(0,1);
    varargout{2} = dK;
  end
  end

end

package com.brahalla.Cerberus.model.security;

import com.fasterxml.jackson.annotation.JsonIgnore;

import java.util.Collection;
import java.util.Date;

import org.springframework.security.core.GrantedAuthority;
import org.springframework.security.core.userdetails.UserDetails;

public class CerberusUser implements UserDetails {

  private Long id;
  private String username;
  private String password;
  private String email;
  private Date lastPasswordReset;
  private Collection<? extends GrantedAuthority> authorities;
  private Boolean accountNonExpired = true;
  private Boolean accountNonLocked = true;
  private Boolean credentialsNonExpired = true;
  private Boolean enabled = true;

  public CerberusUser() {
    super();
  }

  public CerberusUser(Long id, String username, String password, String email, Date lastPasswordReset, Collection<? extends GrantedAuthority> authorities) {
    this.setId(id);
    this.setUsername(username);
    this.setPassword(password);
    this.setEmail(email);
    this.setLastPasswordReset(lastPasswordReset);
    this.setAuthorities(authorities);
  }

  public Long getId() {
    return this.id;
  }

  public void setId(Long id) {
    this.id = id;
  }

  public String getUsername() {
    return this.username;
  }

  public void setUsername(String username) {
    this.username = username;
  }

  @JsonIgnore
  public String getPassword() {
    return this.password;
  }

  public void setPassword(String password) {
    this.password = password;
  }

  public String getEmail() {
    return this.email;
  }

  public void setEmail(String email) {
    this.email = email;
