/// Stream with compatible formats.
struct Stream
{
  Stream(const AudioObjectID stream, std::vector<AudioStreamBasicDescription> &&formats)
  : _stream(stream)
  , _formats(std::move(formats))
  { }

  AudioObjectID _stream;
